import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom'
import axios from 'axios';


const URL_PRODUCTOS = 'http://localhost:8000';


function PaginaPrincipal() {

    const [productos, setProductos] = useState([]);
    const [isLogged, setIsLogged] = useState(false)
    const [isAdmin, setIsAdmin] = useState(false)

    useEffect(() => {
        const loggedUserJSON = window.localStorage.getItem('loggedUser')
        if (loggedUserJSON) {
            setIsLogged(true)
            const carro_usuario = JSON.parse(loggedUserJSON)
            console.log(carro_usuario)
            console.log(carro_usuario.usuario.id)
            const admin = carro_usuario.usuario.is_staff
            setIsAdmin(admin)
            const idCarro = carro_usuario.carro.id
            setCarro(idCarro)
            getLengthCarro(idCarro)
        }
        getPublicaciones()
    }, []);

    const getPublicaciones = async () => {
        const res = await axios.get(`${URL_PRODUCTOS}/api/publicaciones/`, {
            headers: {
                'Content-Type': 'application/json',
            },
        })
        setProductos(res.data)
        console.log(res.data)
    }

    return (
        <div className="container p-4">
            <div className="modal-body row">
                <div className="col-md-10">
                    <h1>Feed</h1>
                    <ol className="list-group">
                        {
                            productos.map(
                                (producto) =>
                                    <li className="list-group-item d-flex justify-content-between align-items-start" key={producto.id}>
                                        <div className="ms-2 me-auto">
                                            <div className="fw-bold">
                                                <img
                                                    styles={{ minWidth: 100, minHeight: 100 }}
                                                    src={`${producto.foto}`}
                                                />
                                            </div>
                                        </div>
                                        <div className="p-1">
                                            <div className="fw-bold">
                                                {`${producto.descripcion}`}
                                            </div>
                                        </div>
                                        <button type="button" className="btn btn-success">Comentar</button>
                                    </li>
                            )
                        }
                    </ol>
                </div>
                <div className="col-md-2 text-right">
                </div>
            </div>
        </div>
    )
}

export default PaginaPrincipal
