import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom'
import axios from 'axios';
import Comentar from './popup/Comentar';


const URL = 'http://localhost:8000';


function PaginaPrincipal() {

    const [publicaciones, setPublicaciones] = useState([])
    const [usuarios, setUsuarios] = useState([])
    const [publicacionId, setPublicacionId] = useState("")
    const [isOpen, setIsOpen] = useState(false)

    useEffect(() => {
        getUsuarios()
        getPublicaciones()
    }, []);

    const getPublicaciones = async () => {
        const res = await axios.get(`${URL}/api/publicaciones/`, {
            headers: {
                'Content-Type': 'application/json',
            },
        })
        setPublicaciones(res.data)
        console.log(res.data)
    }

    const getUsuarios = async () => {
        const res = await axios.get(`${URL}/api/usuarios/`, {
            headers: {
                'Content-Type': 'application/json',
            },
        })
        setUsuarios(res.data)
        console.log(res.data)
    }

    const getOwner = (owner_id) => {
        const owner = usuarios.find(element => element.id === owner_id);
        console.log(owner.username)
        return owner.username
    }

    const tooglePopup = (id) => {
        setPublicacionId(id)
        setIsOpen(!isOpen);
    }

    return (
        <div className="container p-4">
            <div className="modal-body row">
                <div className="col-md-10">
                    <h1>Feed</h1>
                    <ol className="list-group">
                        {
                            publicaciones.map(
                                (publicacion) =>
                                    <li className="list-group-item d-flex justify-content-between align-items-start" key={publicacion.id}>
                                        <div className="ms-2 me-auto">
                                            {`${getOwner(publicacion.owner)}`}
                                            <div className="fw-bold">
                                                <img
                                                    styles={{ minWidth: 100, minHeight: 100 }}
                                                    src={`${publicacion.foto}`}
                                                />
                                            </div>
                                        </div>
                                        <div className="p-1">
                                            <div className="fw-bold">
                                                {`${publicacion.descripcion}`}
                                            </div>
                                        </div>
                                        <div>
                                            <button className="btn btn-secondary" onClick={(e) => tooglePopup(publicacion.id)}>Comentar</button>
                                        </div>
                                    </li>
                            )
                        }
                        {isOpen &&
                            <div>
                                <Comentar idPublicacion={publicacionId} setIsOpen={setIsOpen} />
                            </div>
                        }
                    </ol>
                </div>
                <div className="col-md-2 text-right">
                </div>
            </div>
        </div>
    )
}

export default PaginaPrincipal
