import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom'
import axios from 'axios';


const URL = 'http://localhost:8000';


function PaginaPrincipal() {

    const [productos, setProductos] = useState([])
    const [usuarios, setUsuarios] = useState([])
    const [isLogged, setIsLogged] = useState(false)
    const [isAdmin, setIsAdmin] = useState(false)

    useEffect(() => {
        getUsuarios()
        getPublicaciones()
    }, []);

    const getPublicaciones = async () => {
        const res = await axios.get(`${URL}/api/publicaciones/`, {
            headers: {
                'Content-Type': 'application/json',
            },
        })
        setProductos(res.data)
        console.log(res.data)
    }

    const getUsuarios = async () => {
        const res = await axios.get(`${URL}/api/usuarios/`, {
            headers: {
                'Content-Type': 'application/json',
            },
        })
        setUsuarios(res.data)
        console.log(res.data)
    }

    const getOwner = (owner_id) => {
        const owner = usuarios.find(element => element.id === owner_id);
        console.log(owner.username)
        return owner.username
    }

    return (
        <div className="container p-4">
            <div className="modal-body row">
                <div className="col-md-10">
                    <View>
                        <Header />
                        <ScrollView>
                            <View>
                                <ItemSquareDisplay
                                    items={this.props.items && this.props.items.length > 0
                                        ? this.props.items
                                        : ''} />
                            </View>
                        </ScrollView>
                        <Button style={styles.buttonStyle}>
                            <Text style={styles.buttonTextStyle}>+</Text>
                        </Button>
                    </View>
                    <h1>Feed</h1>
                    <ol className="list-group">
                        {
                            productos.map(
                                (producto) =>
                                    <li className="list-group-item d-flex justify-content-between align-items-start" key={producto.id}>
                                        <div className="ms-2 me-auto">
                                            {`${getOwner(producto.owner)}`}
                                            <div className="fw-bold">
                                                <img
                                                    styles={{ minWidth: 100, minHeight: 100 }}
                                                    src={`${producto.foto}`}
                                                />
                                            </div>
                                        </div>
                                        <div className="p-1">
                                            <div className="fw-bold">
                                                {`${producto.descripcion}`}
                                            </div>
                                        </div>
                                        <button type="button" className="btn btn-success">Comentar</button>
                                    </li>
                            )
                        }
                    </ol>
                </div>
                <div className="col-md-2 text-right">
                </div>
            </div>
        </div>
    )
}

export default PaginaPrincipal
