import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom'
import axios from 'axios';


const URL_PRODUCTOS = 'http://localhost:8000';


function PaginaPrincipal() {

    const [productos, setProductos] = useState([]);

    const res = axios.get(`${URL_PRODUCTOS}/api/publicaciones/`, {
        headers: {
            'Content-Type': 'application/json',
        },
    });
    console.log(res.data);
    setProductos(res.data);

    return (
        <div className="container p-4">
            <div className="modal-body row">
                <div className="col-md-10">
                    <h1>Productos</h1>
                    <h2>Lista de Productos</h2>
                    <ol className="list-group">
                        {
                            productos.map(
                                (producto) =>
                                    <li className="list-group-item d-flex justify-content-between align-items-start" key={producto.id}>
                                        <div className="ms-2 me-auto">
                                            <div className="fw-bold">
                                                <Link to={`/producto/${producto.id}`}>{producto.nombre}</Link>
                                            </div>
                                        </div>
                                        <div className="p-1">
                                            <input type="number" className="col-lg-2" defaultValue={`${1}`} />
                                        </div>
                                        <button type="button" className="btn btn-success">Add to cart</button>
                                    </li>
                            )
                        }
                    </ol>
                </div>
                <div className="col-md-2 text-right">
                </div>
            </div>
        </div>
    )
}

export default PaginaPrincipal
